import useSound from 'use-sound';
import kick from '../drum hits/01bd.wav'
import snare from '../drum hits/02sd.wav'

function Sounds() {

  const [playKick] = useSound(kick)
  const [playSnare] = useSound(snare)


  return (
    <div>
      <h2>Sounds</h2>
      <button id="kick-button" onClick={playKick}>Kick</button>
      <button id="snare-button" onClick={playKick}>Snare</button>
      <button id="closed-hi-hat-button" onClick={playKick}>Closed Hi-Hat</button>
      <button id="open-hi-hat-button" onClick={playKick}>Open Hi-Hat</button>
      <button id="low-tom-button" onClick={playKick}>Low Tom</button>
      <button id="high-tom-button" onClick={playKick}>High Tom</button>
      <button id="cymbal-button" onClick={playKick}>Cymbal</button>
    </div>
  )
}

export default Sounds;
